DROP TABLE tbl_club;
CREATE TABLE tbl_club ( clubid int(10) NOT NULL AUTO_INCREMENT, name varchar(100) NOT NULL COMMENT '俱乐部名称', othername varchar(300) COMMENT '其他名称', countery varchar(50) COMMENT '国家', homepark varchar(100) COMMENT '主球场', coach varchar(30) COMMENT '主教练', coachid int(10) COMMENT '教练id', createdate date COMMENT '俱乐部创建时间', remark text COMMENT '备注', PRIMARY KEY (clubid), CONSTRAINT tbl_club_un_index UNIQUE (name, countery), CONSTRAINT tbl_club_ix_name_countery UNIQUE (name, countery), INDEX tbl_club_ix_name (name) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='俱乐部表';
DROP TABLE tbl_event;
CREATE TABLE tbl_event ( eventid int(10) NOT NULL AUTO_INCREMENT, type varchar(30) COMMENT '事件类型', title varchar(200) COMMENT '事件标题', createtime date COMMENT '创建时间', key1 varchar(50) COMMENT '字段1', key2 varchar(50) COMMENT '字段2', key3 varchar(50) COMMENT '字段3', key4 varchar(50) COMMENT '字段4', message varchar(500) COMMENT '信息', PRIMARY KEY (eventid), INDEX tbl_event_ix1 (type, key1), INDEX tbl_event_ix2 (key1) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='事件表';
DROP TABLE tbl_game;
CREATE TABLE tbl_game ( gameid int(10) NOT NULL AUTO_INCREMENT, matchname varchar(30) COMMENT '联赛名称', matchid int(10) COMMENT '联赛id', home varchar(100) COMMENT '主队名称', homeid int(10) COMMENT '主队id', visit varchar(100) COMMENT '客队名称', visitid int(10) COMMENT '客队id', disdate date COMMENT '比赛时间', bf varchar(100) COMMENT '比分(半场，全场0,1,1,1)', remark text COMMENT '比赛备注', PRIMARY KEY (gameid) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='比赛表';
DROP TABLE tbl_match;
CREATE TABLE tbl_match ( matchid int(10) NOT NULL AUTO_INCREMENT, name varchar(100) NOT NULL COMMENT '联赛名称', othrename varchar(300) COMMENT '其他名称', remark text COMMENT '备注', countery varchar(100) NOT NULL COMMENT '国家', PRIMARY KEY (matchid), CONSTRAINT tbl_match_ix1 UNIQUE (name, countery), INDEX tbl_match_ix2 (name), INDEX tbl_match_ix3 (name, countery) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='联赛表';
DROP TABLE tbl_score;
CREATE TABLE tbl_score ( scoreid int(10) DEFAULT '0' NOT NULL COMMENT '积分id', gradenum int(3) COMMENT '排名', matchname varchar(50) COMMENT '联赛名称', matchid int(10) COMMENT '联赛id', clubname varchar(50) COMMENT '俱乐部名称', clubid int(10) COMMENT '俱乐部id', gamenum int(3) COMMENT '比赛场数', score int(3) COMMENT '积分', win int(3) COMMENT '胜num', pin int(3) COMMENT '平num', fu int(3) COMMENT '负num', winball int(3) COMMENT '进球num', fuball int(3) COMMENT '输球num', cleanball int(3) COMMENT '净胜球num', scoretype varchar(30) COMMENT '积分类型', issue varchar(30) COMMENT '赛季', lun varchar(30) COMMENT '比赛轮次', numshow int(1) COMMENT '0 当前积分 1,历史积分', createtime date COMMENT '创建时间', remark text, PRIMARY KEY (scoreid), CONSTRAINT tbl_score_un_index UNIQUE (matchid, issue, lun, scoretype, gradenum), CONSTRAINT tbl_score_ix3 UNIQUE (matchid, numshow), INDEX tbl_score_ix2 (matchid, scoretype, issue, lun, numshow) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='积分榜表';
