insert into tbl_event (eventid, type, title, createtime, key1, key2, key3, key4, message) values (4, 'Game_RESULT', '西布朗VS阿森纳 1:1(平)', '2013-10-07', '2', '1', null, null, null);
insert into tbl_game (gameid, matchname, matchid, home, homeid, visit, visitid, disdate, bf, remark) values (1, '英超', 1, '西布朗', 1, '阿森纳', 2, '2013-10-06', '1:0,1:1', '西布朗上半场，42分雅科布进1球。 下半场，攻势很猛。 阿森纳下半场，62分威尔谢尔进1球，将比分扳平。 阿森纳士气很足。  总体上看，西布朗在本次排名8~9，与排名第一的球队站完全不落下风。 组织了很多次的进攻，单进球效率比较低。有很多进球的机会都不能命中球门。  两只球队的攻击力都很强，单式防守似乎有一些缺陷，传球也是比较大，很快就能冲到对方球门。 如果和一些顶级球队，拜仁这些防守严密的球队比赛，攻势是否可以这么好的打起来。  总结：从比分还有比赛视频来看，两只球队虽然名次有10名相差，但技术，球队状态相差不是很明显。 接下来如果曼联这些英超球队真正苏醒后，这两只球队是否可以保持现在的成绩，还要待定。  分析:http://www.gboooo.com/zh_CN/analysis/1553668.html 比赛:http://www.gboooo.com/zh_CN/db/rt_info/1553668.html ');
insert into tbl_match (matchid, name, othrename, remark, countery) values (1, '英超', '英格兰足球超级联赛', '英格兰足球超级联赛（简称英超）是英格兰足总属下的职业足球联赛，成立于1992年2月，前身是英格兰甲级联赛，已成为欧洲五大主流联赛之一，由20支球队组成，成立至今以曼联的战绩最佳。', '英国');
